<?php

namespace App\Views;

use League\Plates\Engine;
use League\Plates\Extension\ExtensionInterface;

class CinergieBootstrapIcons implements ExtensionInterface
{
    private $icons;

    private CONST SVG_TAG_TITLE = '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16" %s><title>%s</title>%s</svg>';

    public function register(Engine $engine)
    {
        $this->icons = $this->loadIcons();
        $engine->registerFunction('bi', [$this, 'icon']);
    }

    public function icon(string $purpose = null, $html_attributes = []): string
    {
        $html_attributes['class'] = 'bi ' . ($html_attributes['class'] ?? '');

        $title = $html_attributes['title'] ?? $purpose ?? '';

        return sprintf(self::SVG_TAG_TITLE, $this->flattenAttributes($html_attributes), $title, $this->pathFor($purpose));
    }

    private function flattenAttributes(array $html_attributes): string
    {
        return implode(' ', array_map(function ($key, $value) {
            return $key . '="' . $value . '"';
        }, array_keys($html_attributes), $html_attributes));
    }

    private function pathFor(string $purpose): string
    {
        return $this->icons[$purpose] ?? $this->defaultIcon();
    }

    private function defaultIcon(): string
    {
        return '';
    }

