<?php

namespace App\Views;

use League\Plates\Engine;
use League\Plates\Extension\ExtensionInterface;

class CinergieStreamlineIcons implements ExtensionInterface
{
    private $icons;

    public function register(Engine $engine)
    {
        $this->icons = $this->loadIcons('StreamLine');
        $engine->registerFunction('icon', [$this, 'icon']);
    }

    public function icon(string $purpose = null, int $dimension = 14, $html_attributes = []): string
    {

        $html_attributes['width'] ??= $dimension;
        $html_attributes['height'] ??= $dimension;

        $html_attributes['class'] ??= '';
        $html_attributes['class'] .= ' a';
        

        return sprintf('<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" %s>', $this->flattenAttributes($html_attributes))
            .  sprintf('<defs><style>.a{fill:none;stroke:currentColor;stroke-linecap:round;stroke-linejoin:round;stroke-width:1.5px;}</style></defs><title>%s</title>', $purpose)
            . $this->svgFor($purpose)
            . '</svg>';
    }

    private function flattenAttributes(array $html_attributes): string
    {
        return implode(' ', array_map(function ($key, $value) {
            return $key . '="' . $value . '"';
        }, array_keys($html_attributes), $html_attributes));
    }

    private function svgFor(string $purpose): string
    {
        return $this->icons[$purpose] ?? $this->defaultIcon();
    }

